mapper.update:
update guest.guest g left join guest.check_in as c on g.id_card = c.guest_id_card
set g.contact = #{contact},
    c.room_id = #{roomId},
    c.state   = #{state},
    g.name    = #{name}
where g.id_card = #{idCard}

mapper.select:
select *
from guest
where id_card like "%"#{idCard}"%";

select *
from guest
where contact like "%"#{contact}"%";
